import React, { useState } from "react";
import axios from "axios";


function AddExpense() {
  const [name, setName] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(""); // Date field for manual entry
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    const newExpense = { name, amount, date };

    // Validate date format (optional)
    const datePattern = /^\d{2}-\d{2}-\d{4}$/; // format dd-mm-yyyy
    if (!datePattern.test(date)) {
      alert("Please enter the date in dd-mm-yyyy format");
      return;
    }

    axios
      .post("http://localhost:8080/api/expenses", newExpense)
      .then((response) => {
        history.push("/");
      })
      .catch((error) => console.log("Error adding expense:", error));
  };

  return (
    <div>
      <h2>Add New Expense</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Date (dd-mm-yyyy):</label>
          <input
            type="text"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
            placeholder="dd-mm-yyyy"
          />
        </div>
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
}

export default AddExpense;
