import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from "moment";


function AddExpense() {
  const [name, setName] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date()); 
  const [error, setError] = useState(""); 
  const [successMessage, setSuccessMessage] = useState(""); 
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formattedDate = moment(date).format("YYYY-MM-DD"); // Ensure correct date format
    const newExpense = { name, amount: Number(amount), date: formattedDate }; // Convert amount to number

    try {
      const response = await axios.post("http://localhost:8080/api/expenses", newExpense);
      setSuccessMessage("Expense added successfully!");
      setError(""); 
      console.log("Expense added:", response.data);
      navigate("/expense-list"); 
    } catch (error) {
      setError("Error adding expense. Please try again.");
      setSuccessMessage("");
      console.error("Error:", error.response ? error.response.data : error.message);
    }
  };

  return (
    <div>
      <h2>Add New Expense</h2>
      {error && <div style={{ color: "red" }}>{error}</div>}
      {successMessage && <div style={{ color: "green" }}>{successMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Date:</label>
          <DatePicker
            selected={date}
            onChange={(newDate) => setDate(newDate)}
            dateFormat="yyyy-MM-dd"
            maxDate={new Date()} 
            showYearDropdown
            scrollableYearDropdown
          />
        </div>
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
}

export default AddExpense;
