import React, { useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import Register from "./Component/Register";
import Login from "./Component/Login";
import Dashboard from "./Component/Dashboard";
import ExpenseList from "./Component/ExpenseList";  // Assuming this is your expense list component
import AddExpense from "./Component/AddExpense";  // Assuming this is your add expense component
import EditExpense from "./Component/EditExpense";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status

  return (
    <div>
      {/* Navigation links */}
      <div>
        {!isLoggedIn ? (
          <>
            <Link to="/register">Register</Link> | 
            <Link to="/login">Login</Link>
          </>
        ) : (
          <>
            <Link to="/dashboard">Dashboard</Link> | 
            <Link to="/add-expense">Add Expense</Link> | 
            <Link to="/expense-list">Expense List</Link>
          </>
        )}
      </div>

      {/* Routes */}
      <Routes>
        <Route path="/" element={<Register />} />  {/* Default Page */}
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/add-expense" element={<AddExpense />} />
        <Route path="/expense-list" element={<ExpenseList />} /> {/* Expense List page */}
        <Route path="/" element={<ExpenseList />} /> {/* Expense List Route */}
        <Route path="/edit-expense/:id" element={<EditExpense />} />
      </Routes>
    </div>
  );
};

export default App;
