import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = () => {
  const [credentials, setCredentials] = useState({
    username: "",
    password: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/api/auth/login", credentials);
      
      // Check if response and response.data are defined
      if (response && response.data) {
        if (response.data === "Login successful!") {
          alert("Login successful!");
          navigate("/dashboard");
        } else {
          alert(response.data);  // Show the error message from the backend
        }
      } else {
        // If no data in response
        alert("Unexpected error occurred. Please try again later.");
      }
    } catch (error) {
      // Handle error from Axios request or backend
      if (error.response) {
        // Error response from server
        alert("Error: " + error.response.data || "An error occurred!");
      } else if (error.request) {
        // Request was made but no response
        alert("Error: No response from the server. Please try again later.");
      } else {
        // Something else went wrong
        alert("Error: " + error.message);
      }
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit}>
        <h2>Login</h2>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={credentials.username}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={credentials.password}
          onChange={handleChange}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
