import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";

const Register = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    password: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log("Submitting registration data", user);
  
      // Make the POST request to the backend
      const response = await axios.post("http://localhost:8080/api/auth/register", user);
  
      console.log("Server response", response);
  
      // Check if the response status is 200 and the response message is what we expect
      if (response.status === 200 && response.data === "Registration successful!") {
        alert("Registration successful!");
        navigate("/login");  // Redirect to login page after successful registration
      } else {
        alert("Registration failed! Please check your inputs or try again later.");
        console.log("Response data:", response.data);  // Add this to inspect the response data
      }
    } catch (error) {
      console.error("Error occurred during registration", error);
      alert("Registration failed! Please check your inputs or try again later.");
    }
  };

  return (
    <div className="container">
      <div className="form-container">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={user.username}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={user.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={user.password}
            onChange={handleChange}
            required
          />
          <button type="submit">Register</button>
        </form>
        <p>
          Already have an account? <a href="/login">Login</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
