import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ExpenseList.css"; // Import the CSS file

const ExpenseList = () => {
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/expenses/all");
        setExpenses(response.data);
      } catch (error) {
        console.error("Error fetching expenses:", error);
      }
    };

    fetchExpenses();
  }, []);

  return (
    <div className="expense-container">
      <h2>Expense List</h2>
      <table className="expense-table">
        <thead>
          <tr>
            <th>Category</th> {/* Replaced Item Name with Category */}
            <th>Amount</th>
            <th>Date</th>
            <th>Update</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((expense) => (
            <tr key={expense.id}>
              <td>{expense.category}</td> {/* Changed from expense.name to expense.category */}
              <td>â‚¹{expense.amount}</td>
              <td>{expense.date}</td>
              <td>
                <button className="update-btn">Update</button>
                <button className="delete-btn">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ExpenseList;
