import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [expenses, setExpenses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/expenses/all', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('userId')}`,
          },
        });
        setExpenses(response.data);
      } catch (error) {
        alert('Error fetching expenses');
      }
    };
    fetchExpenses();
  }, []);

  const handleEdit = (id) => {
    navigate(`/edit-expense/${id}`);
  };

  const handleDelete = async (id) => {
    try {
      const response = await axios.delete(`http://localhost:8080/api/expenses/delete/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('userId')}`,
        },
      });
      if (response.status === 200) {
        setExpenses(expenses.filter(expense => expense.id !== id));
      } else {
        alert('Failed to delete expense');
      }
    } catch (error) {
      alert('Error deleting expense');
    }
  };

  return (
    <div>
      <h2>Dashboard</h2>
      <button onClick={() => navigate('/add-expense')}>Add New Expense</button>
      <ul>
        {expenses.map((expense) => (
          <li key={expense.id}>
            {expense.description} - {expense.amount} - {expense.date}
            <button onClick={() => handleEdit(expense.id)}>Edit</button>
            <button onClick={() => handleDelete(expense.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
