import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import moment from "moment";
import './ExpenseList.css'; // Import the CSS file

function ExpenseList() {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if it's the first time the user is visiting
    const isFirstVisit = localStorage.getItem("isFirstVisit");

    if (!isFirstVisit) {
      // If it's the first visit, set the flag in localStorage
      localStorage.setItem("isFirstVisit", "false");
    }

    // Fetching expenses for existing users or first-time users
    axios
      .get("http://localhost:8080/api/expenses/all")
      .then((response) => {
        setExpenses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching expenses");
        setLoading(false);
      });
  }, []);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:8080/api/expenses/delete/${id}`)
      .then((response) => {
        setExpenses(expenses.filter((expense) => expense.id !== id));
      })
      .catch((error) => console.log("Error deleting expense:", error));
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
    }).format(amount);
  };

  const formatDate = (date) => {
    return moment(date).format("DD MMM YYYY"); // Format the date as you wish
  };

  if (loading) {
    return <div className="loading-message">Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="expense-list-container">
      <h2 className="expense-list-heading">All Expenses</h2>
      {expenses.length === 0 ? (
        <div className="no-expenses-message">
          You don't have any expenses yet. Start by adding your first expense!
        </div>
      ) : (
        <>
          <Link to="/add-expense" className="add-expense-link">Add New Expense</Link>
          <table className="expense-list-table">
            <thead>
              <tr>
                <th>Expense</th>
                <th>Amount</th>
                <th>Date</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((expense) => (
                <tr key={expense.id}>
                  <td>{expense.name}</td> {/* Expense Name column */}
                  <td>{formatCurrency(expense.amount)}</td> {/* Amount column */}
                  <td>{formatDate(expense.date)}</td> {/* Date column */}
                  <td>
                    <Link to={`/edit-expense/${expense.id}`} className="edit-link">Edit</Link>
                  </td> {/* Edit column */}
                  <td>
                    <button className="button" onClick={() => handleDelete(expense.id)}>Delete</button>
                  </td> {/* Delete column */}
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default ExpenseList;
