import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import moment from "moment";
import "./ExpenseList.css";

function ExpenseList() {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = localStorage.getItem("userId"); // Assuming user ID is stored in local storage
  const navigate = useNavigate();

  useEffect(() => {
    if (!userId) {
      setError("User not logged in");
      setLoading(false);
      return;
    }

    axios
      .get(`http://localhost:8080/api/expenses/user/${userId}`)
      .then((response) => {
        setExpenses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching expenses");
        setLoading(false);
      });
  }, [userId]);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:8080/api/expenses/delete/${id}`)
      .then(() => {
        setExpenses(expenses.filter((expense) => expense.id !== id));
      })
      .catch((error) => console.log("Error deleting expense:", error));
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
    }).format(amount);
  };

  if (loading) {
    return <div className="loading-message">Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="expense-list-container">
      <h2 className="expense-list-heading">Your Expenses</h2>
      <Link to="/add-expense" className="add-expense-link">Add New Expense</Link>

      <table className="expense-table">
        <thead>
          <tr>
            <th>Expense Name</th>
            <th>Amount</th>
            <th>Date</th>
            <th>Operations</th>
          </tr>
        </thead>
        <tbody>
          {expenses.length === 0 ? (
            <tr>
              <td colSpan="4" className="no-expense-message">No expenses found</td>
            </tr>
          ) : (
            expenses.map((expense) => (
              <tr key={expense.id}>
                <td>{expense.name}</td>
                <td>{formatCurrency(expense.amount)}</td>
                <td>{moment(expense.date).format("YYYY-MM-DD")}</td>
                <td>
                  <Link to={`/edit-expense/${expense.id}`} className="edit-button">Edit</Link>
                  <button className="delete-button" onClick={() => handleDelete(expense.id)}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default ExpenseList;
