import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddExpense = () => {
  const [name, setName] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(""); // New state for date
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId"); // Get userId from localStorage

  // Check if userId is not available
  if (!userId) {
    // Redirect to login page if userId is not set
    navigate("/login");
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!userId) {
      alert("User not logged in. Please login again.");
      navigate("/login");
      return;
    }

    const newExpense = {
      name,
      amount: parseFloat(amount),
      date, // Include date in the request
    };

    axios
      .post(`http://localhost:8080/api/expenses/user/${userId}`, newExpense)
      .then(() => {
        alert("Expense added!");
        navigate("/expense-list"); // Redirect to the expense list page after successful addition
      })
      .catch((error) => {
        alert("Error adding expense");
        console.log(error);
      });
  };

  return (
    <div>
      <h2>Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Name" 
          value={name} 
          onChange={(e) => setName(e.target.value)} 
          required 
        />
        <input 
          type="number" 
          placeholder="Amount" 
          value={amount} 
          onChange={(e) => setAmount(e.target.value)} 
          required 
        />
        <input 
          type="date" 
          value={date} 
          onChange={(e) => setDate(e.target.value)} 
          required 
        />
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
};

export default AddExpense;
