import React from "react";
import { Routes, Route, useLocation } from "react-router-dom";
import Register from "./Component/Register";
import Login from "./Component/Login";
import Dashboard from "./Component/Dashboard";
import AddExpense from "./Component/AddExpense";
import ExpenseList from "./Component/ExpenseList";
import EditExpense from "./Component/EditExpense";
import Footer from "./Component/Footer";
import Navbar from "./Component/Navbar";
import LandingPage from "./Component/LandingPage"; // Import the LandingPage component

function App() {
  const location = useLocation();

  return (
    <div className="app-container">
      {/* Navbar will be shown on all pages */}
      <Navbar />

      <div className="main-content">
        <Routes>
          {/* Set the landing page as the default route */}
          <Route path="/" element={<LandingPage />} />
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/add-expense" element={<AddExpense />} />
          <Route path="/expense-list" element={<ExpenseList />} />
          <Route path="/edit-expense/:id" element={<EditExpense />} />
        </Routes>
      </div>

      {/* Render the Footer only on the LandingPage */}
      {location.pathname === "/" && <Footer />}
    </div>
  );
}

export default App;
