import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    const credentials = { username, password };

    try {
      const response = await axios.post("http://localhost:8080/api/auth/login", credentials);
      
      console.log("Server response:", response.data); // Debugging API response

      // Ensure response has userId or token
      if (response.data.userId) {
        localStorage.setItem("userId", response.data.userId);
        alert("Login successful!");
        navigate("/dashboard");
      } else if (response.data.token) {
        localStorage.setItem("authToken", response.data.token); // If using JWT
        alert("Login successful!");
        navigate("/dashboard");
      } else {
        alert("Invalid login credentials. Please check your username and password.");
      }
    } catch (error) {
      console.error("Login error:", error.response?.data || error.message);

      if (error.response && error.response.status === 401) {
        alert("Invalid login credentials. Please check your username and password.");
      } else {
        alert("Login failed due to server error. Please try again later.");
      }
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
