import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";

const Register = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setError(null);
      console.log("üöÄ Submitting registration data:", user);

      const response = await axios.post("http://localhost:8080/api/auth/register", user);

      console.log("‚úÖ Server response:", response);
      console.log("üîç Server response data:", response.data);

      if (response.status === 200) {
        alert("üéâ Registration successful!");
        console.log("üîÑ Navigating to /login...");

        navigate("/login"); // React Router navigation
        setTimeout(() => {
          window.location.href = "/login"; // Fallback if navigate() fails
        }, 500);
      } else {
        console.error("‚ùå Registration failed! Unexpected response:", response.data);
        setError("Registration failed! Please check your inputs.");
      }
    } catch (error) {
      console.error("‚ùå Error occurred during registration:", error);
      setError("Registration failed! Please try again later.");
    }
  };

  return (
    <div className="container">
      <div className="form-container">
        <h2>Register</h2>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={user.username}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={user.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={user.password}
            onChange={handleChange}
            required
          />
          <button type="submit">Register</button>
        </form>
        <p>
          Already have an account? <a href="/login">Login</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
