import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";


function EditExpense() {
  const { id } = useParams();
  const [expense, setExpense] = useState({ name: "", amount: "" });
  const [loading, setLoading] = useState(true); // For loading state
  const [error, setError] = useState(""); // For error state
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/expenses/${id}`)
      .then((response) => {
        setExpense(response.data);
        setLoading(false); // Stop loading after data is fetched
      })
      .catch((error) => {
        setError("Failed to load expense details.");
        setLoading(false); // Stop loading even in case of an error
        console.log("Error fetching expense:", error);
      });
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (expense.amount <= 0) {
      setError("Amount must be a positive value!");
      return;
    }

    axios
      .put(`http://localhost:8080/api/expenses/update/${id}`, expense)
      .then((response) => {
        navigate("/");  // Navigate to the home page after successful update
      })
      .catch((error) => {
        setError("Failed to update the expense. Please try again.");
        console.log("Error updating expense:", error);
      });
  };

  if (loading) {
    return <div>Loading...</div>; // Show loading message
  }

  return (
    <div>
      <h2>Edit Expense</h2>
      {error && <p style={{ color: "red" }}>{error}</p>} {/* Display error messages */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={expense.name}
            onChange={(e) => setExpense({ ...expense, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            value={expense.amount}
            onChange={(e) => setExpense({ ...expense, amount: e.target.value })}
            required
          />
        </div>
        <button type="submit">Update Expense</button>
      </form>
    </div>
  );
}

export default EditExpense;
