import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    const credentials = { username, password };

    axios
      .post("http://localhost:8080/api/auth/login", credentials)
      .then((response) => {
        // Assuming the response contains a userId along with the success message
        const userId = response.data.userId; // Replace with actual response structure

        if (userId) {
          // Save userId in localStorage
          localStorage.setItem("userId", userId);
          alert("Login successful!");
          navigate("/dashboard"); // Redirect to dashboard or home page after login
        } else {
          alert("Invalid login credentials");
        }
      })
      .catch((error) => {
        alert("Login failed. Please try again.");
        console.log(error);
      });
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
