import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddExpense = () => {
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [date, setDate] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const expenseData = {
      amount,
      category,
      date,
    };

    try {
      // Send POST request to add expense
      const response = await axios.post("http://localhost:8080/api/expenses/add", expenseData);

      // Log the response to check its structure
      console.log("API Response:", response);

      // Ensure the response status and message are as expected
      if (response.status === 200 && response.data && response.data.message === "Expense added successfully") {
        alert("Expense added successfully!"); // Show success alert
        // Redirect after 1 second
        setTimeout(() => {
          navigate("/expense-list");
        }, 1000);
      } else {
        // If message isn't as expected
        alert("Failed to add expense. Please try again.");
      }
    } catch (error) {
      console.error("Error adding expense:", error);
      alert("Error adding expense. Please try again later.");
    }
  };

  return (
    <div>
      <h2>Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Amount</label>
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Category</label>
          <input
            type="text"
            placeholder="Category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <button type="submit">Add Expense</button>
        </div>
      </form>
    </div>
  );
};

export default AddExpense;
