import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

function EditExpense() {
  const { id } = useParams(); // Get the expense ID from URL
  const [expense, setExpense] = useState({ name: "", amount: "", date: "" });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // Use navigate for redirection

  useEffect(() => {
    // Fetch the expense data by ID
    axios
      .get(`http://localhost:8080/api/expenses/${id}`)
      .then((response) => {
        setExpense(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching expense");
        setLoading(false);
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExpense({ ...expense, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Update the expense data
    axios
      .put(`http://localhost:8080/api/expenses/update/${id}`, expense)
      .then(() => {
        navigate("/"); // Redirect to the Expense List page after successful update
      })
      .catch((error) => {
        setError("Error updating expense");
      });
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Edit Expense</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={expense.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="amount">Amount</label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={expense.amount}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="date">Date</label>
          <input
            type="date"
            id="date"
            name="date"
            value={expense.date}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Update Expense</button>
      </form>
    </div>
  );
}

export default EditExpense;
