import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from "moment";


function AddExpense() {
  const [name, setName] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date()); 
  const [error, setError] = useState(""); 
  const [successMessage, setSuccessMessage] = useState(""); 
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = moment(date).format("YYYY-MM-DD"); // Format date before sending
  
    const newExpense = {
      name,
      amount: parseFloat(amount), // Ensure amount is sent as number
      date: formattedDate,
    };
  
    axios
      .post("http://localhost:8080/api/expenses", newExpense)
      .then((response) => {
        setSuccessMessage("Expense added successfully!");
        setError("");
        navigate("/EditExpense");
      })
      .catch((error) => {
        setError("Error adding expense. Please try again.");
        console.log("Error adding expense:", error);
      });
  };
  

  return (
    <div>
      <h2>Add New Expense</h2>
      {error && <div style={{ color: "red" }}>{error}</div>}
      {successMessage && <div style={{ color: "green" }}>{successMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Date:</label>
          <DatePicker
            selected={date}
            onChange={(newDate) => setDate(newDate)}
            dateFormat="yyyy-MM-dd"
            maxDate={new Date()} 
            showYearDropdown
            scrollableYearDropdown
          />
        </div>
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
}

export default AddExpense;
