import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddExpense = () => {
  const [name, setName] = useState("");
  const [amount, setAmount] = useState("");
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId"); // Assuming user is stored in localStorage

  const handleSubmit = (e) => {
    e.preventDefault();
    const newExpense = { name, amount: parseFloat(amount) };

    axios
      .post(`http://localhost:8080/api/expenses/user/${userId}`, newExpense)
      .then(() => {
        alert("Expense added!");
        navigate("/expense-list");
      })
      .catch((error) => {
        alert("Error adding expense");
        console.log(error);
      });
  };

  return (
    <div>
      <h2>Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Name" onChange={(e) => setName(e.target.value)} required />
        <input type="number" placeholder="Amount" onChange={(e) => setAmount(e.target.value)} required />
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
};

export default AddExpense;
