import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate
import moment from "moment"; // Import moment for date formatting

function ExpenseList() {
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true); // State for loading
  const [error, setError] = useState(null); // State for error handling
  const navigate = useNavigate(); // Use useNavigate

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/expenses/all")
      .then((response) => {
        setExpenses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError("Error fetching expenses");
        setLoading(false);
      });
  }, []);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:8080/api/expenses/delete/${id}`)
      .then((response) => {
        setExpenses(expenses.filter((expense) => expense.id !== id));
      })
      .catch((error) => console.log("Error deleting expense:", error));
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
    }).format(amount);
  };

  if (loading) {
    return <div>Loading...</div>; // Display loading message
  }

  if (error) {
    return <div>{error}</div>; // Display error message
  }

  return (
    <div>
      <h2>All Expenses</h2>
      <Link to="/add-expense">Add New Expense</Link>
      <ul>
        {expenses.map((expense) => (
          <li key={expense.id}>
            <span>
              {expense.name} - {formatCurrency(expense.amount)} -{" "}
              {moment(expense.date).format("DD/MM/YYYY")} {/* Format date */}
            </span>
            <button onClick={() => handleDelete(expense.id)}>Delete</button>
            <Link to={`/edit-expense/${expense.id}`}>Edit</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ExpenseList;
