import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddExpense = () => {
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState(""); // New state for category
  const [date, setDate] = useState(""); // New state for date
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId"); // Get userId from localStorage

  // Check if userId is not available
  if (!userId) {
    // Redirect to login page if userId is not set
    navigate("/login");
    return null; // Prevent further rendering if user is not logged in
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId) {
      alert("User not logged in. Please login again.");
      navigate("/login");
      return;
    }

    // Prepare expense data
    const expenseData = {
      amount,
      category,
      date,
      userId, // Attach the userId to the expense data
    };

    try {
      // Send expense data to the backend
      const response = await axios.post("http://localhost:8080/api/expenses/add", expenseData);

      if (response.status === 200 && response.data.message === "Expense added successfully") {
        alert("Expense added successfully!");
        // Redirect to the Expense List page after successful submission
        navigate("/expense-list");
      } else {
        alert("Failed to add expense. Please try again.");
      }
    } catch (error) {
      console.error("Error adding expense:", error);
      alert("Error adding expense. Please try again later.");
    }
  };

  return (
    <div>
      <h2>Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
};

export default AddExpense;
