import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";

function EditExpense() {
  const { id } = useParams();
  const [expense, setExpense] = useState({ name: "", amount: "" });
  const history = useHistory();

  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/expenses/${id}`)
      .then((response) => setExpense(response.data))
      .catch((error) => console.log("Error fetching expense:", error));
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .put(`http://localhost:8080/api/expenses/update/${id}`, expense)
      .then((response) => {
        history.push("/");
      })
      .catch((error) => console.log("Error updating expense:", error));
  };

  return (
    <div>
      <h2>Edit Expense</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={expense.name}
            onChange={(e) => setExpense({ ...expense, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            value={expense.amount}
            onChange={(e) => setExpense({ ...expense, amount: e.target.value })}
            required
          />
        </div>
        <button type="submit">Update Expense</button>
      </form>
    </div>
  );
}

export default EditExpense;
