import React, { useState } from "react";
import axios from "axios";
import "./Login.css";

const Login = () => {
  const [credentials, setCredentials] = useState({
    username: "",
    password: "",
  });

  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to manage login status

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log("Submitting login data", credentials);

      const response = await axios.post(
        "http://localhost:8080/api/auth/login", // Ensure your backend URL is correct
        credentials
      );

      console.log("Server response:", response.data);

      if (response.status === 200 && response.data.message === "Login successful") {
        alert("Login successful!");

        // Update state to indicate that the user is logged in
        setIsLoggedIn(true);

        // Redirect to dashboard automatically after successful login
        window.location.href = "/dashboard";  // Redirect to dashboard
      } else {
        console.log("Login failed:", response.data.message);
        alert(response.data.message || "Login failed. Please try again.");
      }
    } catch (error) {
      if (error.response) {
        console.error("Error response:", error.response);
        alert("Error: " + (error.response.data.message || "Invalid credentials"));
      } else if (error.request) {
        console.error("Error request:", error.request);
        alert("Error: No response from server. Please try again later.");
      } else {
        console.error("Error:", error.message);
        alert("Error: " + error.message);
      }
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit}>
        <h2>Login</h2>

        <input
          type="text"
          name="username"
          placeholder="Username"
          value={credentials.username}
          onChange={handleChange}
          required
        />

        <input
          type="password"
          name="password"
          placeholder="Password"
          value={credentials.password}
          onChange={handleChange}
          required
        />

        <button type="submit">Login</button>
      </form>

      <p>
        Don't have an account? <a href="/register">Register</a>
      </p>

      {/* Conditionally render the anchor link after login success */}
      {isLoggedIn && (
        <p>
          You are logged in! <a href="/dashboard">Go to Dashboard</a>
        </p>
      )}
    </div>
  );
};

export default Login;
