import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate

function ExpenseList() {
  const [expenses, setExpenses] = useState([]);
  const navigate = useNavigate(); // Use useNavigate

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/expenses/getall")
      .then((response) => setExpenses(response.data))
      .catch((error) => console.log("Error fetching expenses:", error));
  }, []);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:8080/api/expenses/delete/${id}`)
      .then((response) => {
        setExpenses(expenses.filter((expense) => expense.id !== id));
      })
      .catch((error) => console.log("Error deleting expense:", error));
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
    }).format(amount);
  };

  return (
    <div>
      <h2>All Expenses</h2>
      <Link to="/add-expense">Add New Expense</Link>
      <ul>
        {expenses.map((expense) => (
          <li key={expense.id}>
            <span>{expense.name} - {formatCurrency(expense.amount)} - {expense.date}</span>
            <button onClick={() => handleDelete(expense.id)}>Delete</button>
            <Link to={`/edit-expense/${expense.id}`}>Edit</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ExpenseList;
