import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddExpense = () => {
  const [category, setCategory] = useState(""); // Category instead of title
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(""); // New state for date
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId"); // Get userId from localStorage

  // Check if userId is not available
  if (!userId) {
    // Redirect to login page if userId is not set
    navigate("/login");
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!userId) {
      alert("User not logged in. Please login again.");
      navigate("/login");
      return;
    }

    const newExpense = {
      category, // Use category instead of title
      amount,
      date,
      userId, // Pass userId to associate expense with user
    };

    axios
      .post("http://localhost:8080/api/expenses/add", newExpense)
      .then((response) => {
        alert("Expense added successfully!");
        navigate("/expense-list"); // Redirect to expense list after success
      })
      .catch((error) => {
        console.error("Error adding expense", error);
        alert("Failed to add expense");
      });
  };

  return (
    <div>
      <h2>Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Category" 
          value={category} 
          onChange={(e) => setCategory(e.target.value)} 
          required 
        />
        <input 
          type="number" 
          placeholder="Amount" 
          value={amount} 
          onChange={(e) => setAmount(e.target.value)} 
          required 
        />
        <input 
          type="date" 
          value={date} 
          onChange={(e) => setDate(e.target.value)} 
          required 
        />
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
};

export default AddExpense;
