import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddExpense = () => {
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [date, setDate] = useState("");
  const navigate = useNavigate(); // Check if you are using React Router v6 for useNavigate

  const userId = localStorage.getItem("userId");

  // Redirect if userId is not available
  useEffect(() => {
    if (!userId) {
      navigate("/login");
    }
  }, [userId, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId) {
      alert("User  not logged in. Please login again.");
      navigate("/login");
      return;
    }

    const expenseData = {
      amount,
      category,
      date,
      userId,
    };

    try {
      const response = await axios.post("http://localhost:8080/api/expenses/add", expenseData);
      console.log("Response:", response);

      if (response.status === 200 && response.data.message === "Expense added successfully") {
        alert("Expense added successfully!"); // Show success alert
        setTimeout(() => {
          navigate("/expense-list"); // Redirect to expense list after 1 second
        }, 1000); // Redirect after 1 second to allow alert to close
      } else {
        console.error("Unexpected response:", response);
        alert("Failed to add expense. Please try again.");
      }
    } catch (error) {
      console.error("Error adding expense:", error);
      alert("Error adding expense. Please try again later.");
    }
  };

  return (
    <div>
      <h2>Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Amount</label>
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Category</label>
          <input
            type="text"
            placeholder="Category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <button type="submit">Add Expense</button>
        </div>
      </form>
    </div>
  );
};

export default AddExpense;
