import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from "moment";

function AddExpense() {
  const [name, setName] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date()); // Initialize with today's date
  const [error, setError] = useState(""); // For displaying error messages
  const [successMessage, setSuccessMessage] = useState(""); // For displaying success messages
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDate = moment(date).format("YYYY-MM-DD"); // Format date before sending

    const newExpense = { name, amount, date: formattedDate };

    console.log("Sending new expense:", newExpense); // Log the data you're sending

    axios
      .post("http://localhost:8080/api/expenses", newExpense)
      .then((response) => {
        setSuccessMessage("Expense added successfully!");
        setError(""); // Clear any previous error
        navigate("/expense-list"); // Redirect to the expense list page
      })
      .catch((error) => {
        setError("Error adding expense. Please try again.");
        setSuccessMessage(""); // Clear any previous success message
        console.log("Error adding expense:", error);
      });
  };

  return (
    <div>
      <h2>Add New Expense</h2>
      {error && <div style={{ color: "red" }}>{error}</div>} {/* Display error */}
      {successMessage && <div style={{ color: "green" }}>{successMessage}</div>} {/* Display success message */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Date:</label>
          <DatePicker
            selected={date}
            onChange={(newDate) => setDate(newDate)}
            dateFormat="dd/MM/yyyy"
            maxDate={new Date()} // Prevents selecting future dates
            showYearDropdown
            scrollableYearDropdown
          />
        </div>
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
}

export default AddExpense;
